import { Component, OnInit } from '@angular/core';

interface ChatMessage {
  text: string;
  sender: 'me' | 'other';
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  ws!: WebSocket;
  messages: ChatMessage[] = [];
  messageInput: string = '';
  roomId: string = '';
  joined = false;
  clientId = Math.random().toString(36).substring(2, 8); // unique ID for each user

  ngOnInit() {
  this.ws = new WebSocket('ws://localhost:8080');

  this.ws.onmessage = (event) => {
    console.log("Event",event)
    try {
      const data = JSON.parse(event.data);

      if (data.clientId !== this.clientId) {
        this.messages.push({ text: data.message, sender: 'other' });
      }
    } catch (e) {
      console.warn('‚ö†Ô∏è Non-JSON message received:', event.data);
      // Optional: show it as a system message if you like
      this.messages.push({ text: event.data, sender: 'other' });
    }
  };
}


  joinRoom() {
    if (this.roomId.trim()) {
      this.ws.send(
        JSON.stringify({
          type: 'join',
          payload: { roomId: this.roomId },
        })
      );
      this.joined = true;
    }
  }

  sendMessage() {
    if (this.messageInput.trim()) {
      // Push locally as "me"
      this.messages.push({ text: this.messageInput, sender: 'me' });

      // Send to WebSocket server
      this.ws.send(
        JSON.stringify({
          type: 'chat',
          payload: {
            message: this.messageInput,
            clientId: this.clientId,
          },
        })
      );

      this.messageInput = '';
    }
  }
}
<div class="container">
  <h1>Chat Room</h1>

  <!-- Join Room -->
  <div *ngIf="!joined" class="join-section">
    <input
      type="text"
      [(ngModel)]="roomId"
      placeholder="Enter Room ID"
    />
    <button (click)="joinRoom()">Join Room</button>
  </div>

  <!-- Chat Room -->
  <div *ngIf="joined" class="chat-box">
    <!-- Display Room ID -->
    <div class="room-info">
      <strong>Room ID:</strong> {{ roomId }}
    </div>

    <div class="messages">
      <div
        *ngFor="let msg of messages"
        class="message"
        [ngClass]="msg.sender"
      >
        {{ msg.text }}

        <!-- Optional: Display attachment if present -->
        <ng-container *ngIf="msg.attachment">
          <img *ngIf="msg.attachment.type === 'image'" [src]="msg.attachment.url" class="attachment-image"/>
          <video *ngIf="msg.attachment.type === 'video'" controls class="attachment-video">
            <source [src]="msg.attachment.url" type="video/mp4" />
          </video>
        </ng-container>
      </div>
    </div>

    <div class="send-section">
      <input
        type="text"
        [(ngModel)]="messageInput"
        placeholder="Type a message..."
        (keyup.enter)="sendMessage()"
      />
      <!-- Placeholder buttons for attachments
      <button (click)="attachImage()">üì∑</button> -->
      <!-- <button (click)="attachVideo()">üé•</button> -->
      <button (click)="sendMessage()">Send</button>
    </div>
  </div>
</div>
